function CreateArea(props) {
    const [note,setNote] = React.useState ({
        title: "",
        content: ""
    });

    function handleChange(event) {
        const{name, value} = event.target;

        setNote(prevNote => {
            return {
                ...prevNote,
                [name]: value
            }
        })
    }

